import { Formik, Field, ErrorMessage } from 'formik';
import React from 'react';

const SimpleFormik = ({ addCard }) => {
  const initialValues = {
    title: '',
    text: '',
  };

  const validateForm = (values) => {
    const errors = {};

    if (!values.title) {
      errors.title = 'Title is required';
    }

    if (!values.text) {
      errors.text = 'Description is required';
    }

    return errors;
  };

  const handleSubmit = (values, { resetForm }) => {
    let isValid = true;

    // Additional validation logic to check if fields have values
    if (!values.title.trim()) {
      isValid = false;
      values.titleError = 'Title is required';
    } else {
      values.titleError = '';
    }

    if (!values.text.trim()) {
      isValid = false;
      values.textError = 'Description is required';
    } else {
      values.textError = '';
    }

    if (isValid) {
      // Proceed with the submission
      addCard(values);
      resetForm();
    }
  };

  return (
    <Formik initialValues={initialValues} validate={validateForm} onSubmit={handleSubmit}>
      {() => (
        <div>
          <h1>Simple Formik</h1>
          <form>
            <label>
              Title:
              <Field type="text" name="title" />
              <ErrorMessage name="title" component="div" className="error" />
              {values.titleError && <div className="error">{values.titleError}</div>}
            </label>

            <br />

            <label>
              Description:
              <Field type="text" name="text" />
              <ErrorMessage name="text" component="div" className="error" />
              {values.textError && <div className="error">{values.textError}</div>}
            </label>

            <br />

            <button type="submit">Submit</button>
          </form>

          <style>{`
            .error {
              color: red;
            }
          `}</style>
        </div>
      )}
    </Formik>
  );
};

export default SimpleFormik;
