import { Formik } from 'formik';
import { add } from 'lodash';
import React, { useState } from 'react';

const SimpleFormik = ({addCard}) => {
    const [initialValues, setInitialValues] = useState({
        title: "",
        description: ""
    });

    const handleCustomSubmit = (values) => {
        addCard(values.title, values.description);
    }

    // const handleTitleChange = (e, setFieldValue) => {
    //     setFieldValue("title", e.target.value);
    // }

    // const handleDescriptionChange = (e, setFieldValue) => {
    //     setFieldValue("description", e.target.value);
    // }

    const validate = (values) => {
        const errors = {}

        if (!values.title) {
            errors.title = "Title is required";
        }

        if (!values.description) {
            errors.description = "Description is required";
        }

        return errors;
    }

    return(
        <Formik
            initialValues={initialValues}
            validate={values => validate(values)}
            onSubmit={(values, {resetForm, setSubmitting}) => {
                setSubmitting(true);
                setTimeout(() => {
                    handleCustomSubmit(values);
                    resetForm();
                    setSubmitting(false);
                }, 1000);
            }}
         >{({values, errors, isSubmitting, handleSubmit, setFieldValue}) => (
            <div>
                <h1>Simple Formik</h1>
                <form onSubmit={handleSubmit}>
                    {isSubmitting && <div>Loading...</div>}
                    <div>
                        <label>Title:&nbsp;</label>
                        <input
                            type="text"
                            id="title"
                            name="title"
                            value={values.title}
                            onChange={(e) => handleTitleChange(e, setFieldValue)}
                        />
                        {errors.title && <p>{`${errors.title}`}</p>}
                    </div>
                    <br/>
                    <div>
                        <label>Description:&nbsp;</label>
                        <input
                            type="text"
                            id="description"
                            name="description"
                            value={values.description}
                            onChange={(e) => setTitle(e.target.value)}
                        />
                        {errors.description && <p>{`${errors.description}`}</p>}
                    </div>
                    <br/>
                    <button type="submit" disabled={isSubmitting}>Submit</button>
                </form>
            </div>
            )}
        </Formik>
    )
}

export default SimpleFormik;
