import { Formik } from 'formik';
import { add } from 'lodash';
import React, { useState } from 'react';


const SimpleFormik = ({addCard}) => {
    const errStyle = {
        color: "red",
        fontSize: "12px",
    };
    const initialFormData = {
        title: null,
        text: null,
        imgSrc: null,
    };
    const formRef = useRef(null);
    // handlers
    const formSubmitHandler = (values, actions) => {
        const { title, text, imgSrc } = values;
        addForm(title, text, imgSrc);
        actions.resetForm();
    };
    const formValidateHandler = ({ title, text, imgSrc }) => {
        const errors = {};
        if (isNullOrEmpty(title)) {
            errors.title = "Title is required.";
        }
        if (isNullOrEmpty(text)) {
            errors.text = "Text is required.";
        }
        if (isNullOrEmpty(imgSrc)) {
            errors.imgSrc = "Image Source is required.";
        }
        return errors;
    };
    const isNullOrEmpty = (text) => {
        return text === null || text.trim() === "";
    };
    return (
        <Formik
            innerRef={formRef}
            initialValues={{ ...initialFormData }}
            onSubmit={formSubmitHandler}
            validate={formValidateHandler}
            validateOnChange={false}
            validateOnBlur={false}
        >
            {({ errors, values, resetForm }) => {
                return (
                    <Form>
                        <Row style={{ marginLeft: "1rem" }}>
                            <FormGroup as={Row}>
                                {/* 1st Column */}
                                <Col>
                                    <Row className="mb-3">
                                        <Col sm={3}>
                                            <FormLabel>Title:</FormLabel>
                                        </Col>
                                        <Col sm={9}>
                                            <Field
                                                className="form-control wj-auto-complete text-end"
                                                type="text"
                                                name="title"
                                            />
                                            <p style={errStyle}>
                                                {errors["title"]}
                                            </p>
                                        </Col>
                                    </Row>
                                    <Row className="mb-3">
                                        <Col sm={3}>
                                            <FormLabel>Text:</FormLabel>
                                        </Col>
                                        <Col sm={9}>
                                            <Field
                                                className="form-control wj-auto-complete text-end"
                                                type="text"
                                                name="text"
                                            />
                                            <p style={errStyle}>
                                                {errors["text"]}
                                            </p>
                                        </Col>
                                    </Row>
                                    <Row className="mb-3">
                                        <Col sm={3}>
                                            <FormLabel>Image Src:</FormLabel>
                                        </Col>
                                        <Col sm={9}>
                                            <Field
                                                className="form-control wj-auto-complete text-end"
                                                type="text"
                                                name="imgSrc"
                                            />
                                            <p style={errStyle}>
                                                {errors["imgSrc"]}
                                            </p>
                                        </Col>
                                    </Row>
                                </Col>
                            </FormGroup>
                        </Row>
                        <div>
                            <Button variant="success" type="submit">
                                Add
                            </Button>
                        </div>
                    </Form>
                );
            }}
        </Formik>
    );
};

export default SimpleFormik;
